        if(n.equals("8")) return "GGRGGRRGGGRRGRRGGGRGGRRRGGRRGRRGGGRGGRRGGGRRGRRRGGRGGRRRGGRRGRRGGGRGGRRGGGRRGRRGGGRGGRRRGGRRGRRRGGRGGRRGGGRRGRRRGGRGGRRRGGRRGRRRGGRGGRRGGGRRGRRGGGRGGRRRGGRRGRRGGGRGGRRGGGRRGRRRGGRGGRRRGGRRGRRRGGRGGRRGGGRRGRRGGGRGGRRRGGRRGRRRGGRGGRRGGGRRGRRRGGRGGRRRGGRRGRR";
